mapper.update:
UPDATE users
SET is_deleted = TRUE
WHERE user_id = #{userId}

UPDATE users
SET department = #{department},
    position = #{position},
    role_desc = #{roleDesc},
    updated_at = #{updatedAt}
WHERE user_id = #{userId}

UPDATE users
SET role_in_company = #{roleInCompany},
    updated_at = #{updatedAt}
WHERE user_id = #{userId}
